const t=document.querySelector("#search");t.addEventListener("input",()=>{const s=t.value;return $.ajax({url:"/search?query="+s,type:"GET",success:function(a){const e=$("#searchresults-list"),r=$(a).find(".search-result-wrapper");e.html(r.html()),e.addClass("active")}}),!1});const c=$("#searchresults-list"),l=$("#search");$(window).on("click",()=>{c.hasClass("active")&&(c.removeClass("active"),l.val(""))});$(".search-input-c").on("click",s=>{s.stopPropagation()});
