const t=document.querySelector("#search"),e=document.querySelector(".drug-list-c");document.addEventListener("click",c=>{c.target!==t&&(e.contains(c.target)||e.classList.contains("active")&&(e.classList.remove("active"),t.value=""))});t.addEventListener("input",()=>{e.classList.contains("active")||e.classList.add("active")});const s=document.querySelector(".drug-list-close-c");s.addEventListener("click",()=>{e.classList.remove("active"),t.value=""});
